mapper.select:
	select 
	from f_day_trade_coin
	where 1=1

 and fcoinid = #{fcoinid}


 and date_format(fcreatetime, '%Y-%m-%d') = date_format(#{fcreatetime}, '%Y-%m-%d')

	order by ${orderField} ${orderDirection}
  limit #{offset},#{limit}

select count(fid) from f_day_trade_coin where 1=1
and fcoinid = #{fcoinid}
and date_format(fcreatetime, '%Y-%m-%d') = date_format(#{fcreatetime}, '%Y-%m-%d')
