mapper.select:
select count(fid)
from f_virtual_capital_operation
where
fuid = #{fuid,jdbcType=INTEGER} and
ftype = #{ftype,jdbcType=INTEGER}

	and fcoinid = #{fcoinid,jdbcType=INTEGER}


	and fstatus = #{fstatus,jdbcType=INTEGER}

select ifnull(sum(ifnull(famount,0) + ifnull(ffees,0) + ifnull(fbtcfees,0)),0)
from
f_virtual_capital_operation
where 1=1

	and fuid = #{fuid}


	and fcoinid = #{coinid}


	and ftype = #{type}


	and fstatus = #{status}


	and fcreatetime >= #{start,jdbcType=VARCHAR}


	and fcreatetime <= #{end,jdbcType=VARCHAR}

select f_virtual_capital_operation.fid, f_virtual_capital_operation.fuid, f_virtual_capital_operation.fcoinid, f_virtual_capital_operation.fcreatetime, f_virtual_capital_operation.famount, f_virtual_capital_operation.ffees, f_virtual_capital_operation.fbtcfees, f_virtual_capital_operation.ftype, f_virtual_capital_operation.fstatus, f_virtual_capital_operation.fupdatetime, f_virtual_capital_operation.fwithdrawaddress, f_virtual_capital_operation.frechargeaddress, f_virtual_capital_operation.funiquenumber, f_virtual_capital_operation.fconfirmations, f_virtual_capital_operation.fhasowner, f_virtual_capital_operation.fblocknumber, f_virtual_capital_operation.version, f_virtual_capital_operation.fsource, f_virtual_capital_operation.fplatform, f_virtual_capital_operation.memo, f_user.floginname, f_user.fnickname, f_user.frealname, system_coin_type.name fcoinname, f_user_score.flevel level, f_admin.fname fadminname from f_virtual_capital_operation left join f_user on f_user.fid = f_virtual_capital_operation.fuid left join system_coin_type on system_coin_type.id = f_virtual_capital_operation.fcoinid left join f_admin on f_admin.fid = f_virtual_capital_operation.fadminid left join f_user_score on f_virtual_capital_operation.fuid = f_user_score.fuid where 1=1 and f_virtual_capital_operation.fuid is not null
and ( f_virtual_capital_operation.fwithdrawaddress like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_virtual_capital_operation.frechargeaddress like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_user.floginname like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_user.fnickname like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_user.femail like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_user.ftelephone like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_user.frealname like concat('%',#{keyword,jdbcType=VARCHAR},'%') or f_user.fid = #{keyword,jdbcType=VARCHAR} )
and f_virtual_capital_operation.fcoinid = #{fcoinid}
and f_virtual_capital_operation.ftype = #{ftype}
and f_virtual_capital_operation.fstatus in
${item}
and f_virtual_capital_operation.fupdatetime = #{start}
and f_virtual_capital_operation.fupdatetime >= #{start}
and f_virtual_capital_operation.fupdatetime <= #{end}
and f_user_score.flevel = 6
order by f_virtual_capital_operation.${orderField} ${orderDirection}
limit #{offset},#{limit}

select count(*)
from
f_virtual_capital_operation left join
f_user on f_user.fid = f_virtual_capital_operation.fuid left join
system_coin_type on system_coin_type.id = f_virtual_capital_operation.fcoinid left join
f_user_score on f_virtual_capital_operation.fuid = f_user_score.fuid
where 1=1 and f_virtual_capital_operation.fuid is not null

	and (
	f_virtual_capital_operation.fwithdrawaddress like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_virtual_capital_operation.frechargeaddress like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_user.floginname like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_user.fnickname like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_user.femail like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_user.ftelephone like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_user.frealname like concat('%',#{keyword,jdbcType=VARCHAR},'%') or
	f_user.fid = #{keyword,jdbcType=VARCHAR}
	)


	and f_virtual_capital_operation.fcoinid = #{fcoinid}


	and f_virtual_capital_operation.ftype = #{ftype}


	and f_virtual_capital_operation.fstatus in

		${item}



	and f_user_score.flevel = 6


	and f_virtual_capital_operation.fupdatetime = #{start}


	and f_virtual_capital_operation.fupdatetime >= #{start}


	and f_virtual_capital_operation.fupdatetime <= #{end}
