mapper.select:
	select 
  from f_reward_code
  where 1=1

  and fuid = #{fuid}


  and fcode = #{fcode}


  and fstate = #{fstate}

  order by fupdatetime desc
  limit #{offset},#{limit}

select count(fid)
  from f_reward_code
  where 1=1

  and fuid = #{fuid}


  and fcode = #{fcode}


  and fstate = #{fstate}

