mapper.select:
select f_user_bankinfo.fid, f_user_bankinfo.fuid, f_user_bankinfo.fname, f_user_bankinfo.fbanknumber, f_user_bankinfo.fbanktype, f_system_bankinfo_withdraw.fcnname as fbanktype_s, f_user_bankinfo.fcreatetime, f_user_bankinfo.fstatus, f_user_bankinfo.version, f_user_bankinfo.init, f_user_bankinfo.faddress, f_user_bankinfo.frealname, f_user_bankinfo.fprov, f_user_bankinfo.fcity, f_user_bankinfo.ftype, f_user_bankinfo.fdist from f_user_bankinfo left join f_system_bankinfo_withdraw on f_user_bankinfo.fbanktype = f_system_bankinfo_withdraw.fid where f_user_bankinfo.fuid = #{fuid,jdbcType=INTEGER}
and f_user_bankinfo.ftype= #{ftype,jdbcType=INTEGER}
and f_user_bankinfo.init=1

    select 
    from f_user_bankinfo
    where 1=1 

	 	and fuid = #{fuid,jdbcType=INTEGER} 


	 	and ftype= #{ftype,jdbcType=INTEGER} 


	 	and init= #{init,jdbcType=BIT} 


	 	and fbanknumber= #{fbanknumber,jdbcType=VARCHAR} 


	  and frealname= #{frealname,jdbcType=VARCHAR}


	 	and fstatus= #{fstatus,jdbcType=VARCHAR}

select ifnull(count(fid),0) from f_user_bankinfo
where 1 = 1

	and fbanknumber = #{banknumber,jdbcType=VARCHAR}


	and init = #{init,jdbcType=BIT}
